import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { FaUser, FaLock } from 'react-icons/fa';

function Login({ darkMode = false, onLogin }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password
      });

      // Store token in localStorage
      localStorage.setItem('token', response.data.token);

      // Call parent login handler
      onLogin(response.data.token);

      // Redirect to todo list
      navigate('/todos');
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div className={`
      min-h-screen flex items-center justify-center
      ${darkMode ? 'bg-gray-900' : 'bg-soft-blue'}
    `}>
      <div className={`
        w-full max-w-md p-8 space-y-6 rounded-2xl shadow-glass
        ${darkMode 
          ? 'bg-gray-800 bg-opacity-70 backdrop-blur-sm' 
          : 'bg-white bg-opacity-70 backdrop-blur-sm'}
      `}>
        <h2 className={`
          text-3xl font-bold text-center
          ${darkMode ? 'text-brand-200' : 'text-brand-700'}
        `}>
          Login
        </h2>

        {error && (
          <div className="bg-red-500 text-white p-3 rounded-lg text-center">
            {error}
          </div>
        )}

        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Email Input */}
          <div className="flex items-center space-x-3">
            <FaUser className={darkMode ? 'text-gray-300' : 'text-gray-600'} />
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Email"
              required
              className={`
                w-full p-3 rounded-lg transition-all duration-300
                ${darkMode 
                  ? 'bg-gray-600 text-white placeholder-gray-400' 
                  : 'bg-brand-50 text-gray-800 placeholder-gray-500'}
                focus:outline-none focus:ring-2 focus:ring-brand-500
              `}
            />
          </div>

          {/* Password Input */}
          <div className="flex items-center space-x-3">
            <FaLock className={darkMode ? 'text-gray-300' : 'text-gray-600'} />
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Password"
              required
              className={`
                w-full p-3 rounded-lg transition-all duration-300
                ${darkMode 
                  ? 'bg-gray-600 text-white placeholder-gray-400' 
                  : 'bg-brand-50 text-gray-800 placeholder-gray-500'}
                focus:outline-none focus:ring-2 focus:ring-brand-500
              `}
            />
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            className={`
              w-full p-3 rounded-xl transition-all duration-300 transform hover:scale-105 shadow-soft
              ${darkMode 
                ? 'bg-brand-600 text-white hover:bg-brand-500' 
                : 'bg-brand-500 text-white hover:bg-brand-600'}
            `}
          >
            Login
          </button>
        </form>

        {/* Signup Link */}
        <div className="text-center">
          <p className={darkMode ? 'text-gray-300' : 'text-gray-600'}>
            Don't have an account? {' '}
            <button 
              onClick={() => navigate('/signup')}
              className={`
                font-bold underline
                ${darkMode 
                  ? 'text-brand-300 hover:text-brand-200' 
                  : 'text-brand-600 hover:text-brand-700'}
              `}
            >
              Sign Up
            </button>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;
