import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import { FaSun, FaMoon, FaPlus } from 'react-icons/fa';
import TodoForm from './components/TodoForm';

const STATUSES = [
  { id: 'todo', title: 'To Do', color: 'bg-blue-100' },
  { id: 'in-progress', title: 'In Progress', color: 'bg-yellow-100' },
  { id: 'done', title: 'Done', color: 'bg-green-100' }
];

function App() {
  const [todos, setTodos] = useState([]);
  const [darkMode, setDarkMode] = useState(false);
  const [isAddingTodo, setIsAddingTodo] = useState(false);

  // Fetch todos from backend
  useEffect(() => {
    const fetchTodos = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/todos');
        setTodos(response.data);
      } catch (error) {
        console.error('Error fetching todos:', error);
      }
    };

    fetchTodos();
  }, []);

  // Add new todo
  const addTodo = async (todoData) => {
    try {
      const response = await axios.post('http://localhost:5000/api/todos', {
        ...todoData,
        status: 'todo'
      });
      setTodos([...todos, response.data]);
      setIsAddingTodo(false);
    } catch (error) {
      console.error('Error adding todo:', error);
    }
  };

  // Delete todo
  const deleteTodo = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/todos/${id}`);
      setTodos(todos.filter(todo => todo._id !== id));
    } catch (error) {
      console.error('Error deleting todo:', error);
    }
  };

  // Update todo status
  const updateTodoStatus = async (todoId, newStatus) => {
    try {
      const response = await axios.put(`http://localhost:5000/api/todos/${todoId}`, { status: newStatus });
      setTodos(todos.map(todo => 
        todo._id === todoId ? response.data : todo
      ));
    } catch (error) {
      console.error('Error updating todo status:', error);
    }
  };

  // Drag and drop handler
  const onDragEnd = (result) => {
    const { destination, source, draggableId } = result;

    if (!destination) return;

    if (
      destination.droppableId === source.droppableId &&
      destination.index === source.index
    ) {
      return;
    }

    const draggedTodo = todos.find(todo => todo._id === draggableId);
    updateTodoStatus(draggableId, destination.droppableId);
  };

  return (
    <div className={`
      min-h-screen font-sans p-6
      ${darkMode ? 'bg-gray-900 text-white' : 'bg-soft-blue'}
    `}>
      {/* Dark Mode Toggle */}
      <div className="absolute top-4 right-4 z-10">
        <button 
          onClick={() => setDarkMode(!darkMode)}
          className={`
            p-3 rounded-full transition-all duration-300 transform hover:scale-110 shadow-soft
            ${darkMode 
              ? 'bg-yellow-500 text-gray-900 hover:bg-yellow-400' 
              : 'bg-brand-100 text-brand-600 hover:bg-brand-200'
            }
          `}
        >
          {darkMode ? <FaSun /> : <FaMoon />}
        </button>
      </div>

      {/* Add Todo Button */}
      {!isAddingTodo && (
        <button 
          onClick={() => setIsAddingTodo(true)}
          className={`
            fixed bottom-6 right-6 p-4 rounded-full shadow-lg z-10 transform hover:scale-110 transition-all
            ${darkMode 
              ? 'bg-brand-700 text-white hover:bg-brand-600' 
              : 'bg-brand-500 text-white hover:bg-brand-600'
            }
          `}
        >
          <FaPlus className="text-2xl" />
        </button>
      )}

      {/* Add Todo Form */}
      {isAddingTodo && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-20">
          <div className="w-full max-w-md">
            <TodoForm 
              onAdd={addTodo} 
              darkMode={darkMode} 
              onCancel={() => setIsAddingTodo(false)} 
            />
          </div>
        </div>
      )}

      {/* Kanban Board */}
      <DragDropContext onDragEnd={onDragEnd}>
        <div className="grid grid-cols-3 gap-4">
          {STATUSES.map(status => (
            <div 
              key={status.id} 
              className={`
                rounded-xl p-4 min-h-[500px] 
                ${status.color} 
                ${darkMode ? 'bg-opacity-10' : 'bg-opacity-50'}
              `}
            >
              <h2 className={`
                text-xl font-bold mb-4 text-center
                ${darkMode ? 'text-gray-200' : 'text-gray-800'}
              `}>
                {status.title}
              </h2>
              <Droppable droppableId={status.id}>
                {(provided) => (
                  <div 
                    {...provided.droppableProps} 
                    ref={provided.innerRef} 
                    className="space-y-3"
                  >
                    {todos
                      .filter(todo => todo.status === status.id)
                      .map((todo, index) => (
                        <Draggable key={todo._id} draggableId={todo._id} index={index}>
                          {(provided) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                              className={`
                                p-4 rounded-lg shadow-soft
                                ${darkMode 
                                  ? 'bg-gray-700 text-gray-200' 
                                  : 'bg-white'}
                              `}
                            >
                              <h3 className="font-semibold">{todo.text}</h3>
                              {todo.description && (
                                <p className="text-sm text-gray-500 mt-1">
                                  {todo.description}
                                </p>
                              )}
                            </div>
                          )}
                        </Draggable>
                      ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </div>
          ))}
        </div>
      </DragDropContext>
    </div>
  );
}

export default App;
