import React, { useState } from 'react';
import { FaTrash, FaEdit, FaCheck, FaTimes } from 'react-icons/fa';
import { motion, AnimatePresence } from 'framer-motion';

function TodoList({ todos, onDelete, onToggleComplete, onEdit, darkMode = false }) {
  const [editingId, setEditingId] = useState(null);
  const [editText, setEditText] = useState('');
  const [filter, setFilter] = useState('all');

  const filteredTodos = todos.filter(todo => {
    if (filter === 'completed') return todo.completed;
    if (filter === 'incomplete') return !todo.completed;
    return true;
  });

  const handleEditStart = (todo) => {
    setEditingId(todo._id);
    setEditText(todo.text);
  };

  const handleEditSubmit = (todo) => {
    onEdit(todo._id, { text: editText });
    setEditingId(null);
  };

  return (
    <div>
      {/* Filter Buttons */}
      <div className="flex justify-center space-x-4 mb-6">
        {['all', 'completed', 'incomplete'].map(filterType => (
          <button
            key={filterType}
            onClick={() => setFilter(filterType)}
            className={`px-4 py-2 rounded-lg transition-colors ${
              filter === filterType 
                ? (darkMode ? 'bg-blue-700 text-white' : 'bg-blue-500 text-white') 
                : (darkMode ? 'bg-gray-700 text-gray-300 hover:bg-gray-600' : 'bg-gray-200 text-gray-700 hover:bg-gray-300')
            }`}
          >
            {filterType.charAt(0).toUpperCase() + filterType.slice(1)}
          </button>
        ))}
      </div>

      {/* Task Count */}
      <div className="text-center mb-4 text-gray-600">
        {filteredTodos.length} task{filteredTodos.length !== 1 ? 's' : ''} {filter !== 'all' && `(${filter})`}
      </div>

      <AnimatePresence>
        <ul className="space-y-3">
          {filteredTodos.map(todo => (
            <motion.li
              key={todo._id}
              initial={{ opacity: 0, x: -50 }}
              animate={{ opacity: 1, x: 0 }}
              exit={{ opacity: 0, x: 50 }}
              transition={{ duration: 0.3 }}
              className={`
                flex items-center justify-between p-4 rounded-lg shadow-md transition-all duration-300
                ${todo.completed 
                  ? (darkMode ? 'bg-green-900 border-l-4 border-green-600 text-green-200' : 'bg-green-50 border-l-4 border-green-500') 
                  : (darkMode ? 'bg-gray-700 hover:bg-gray-600 text-gray-100' : 'bg-white hover:bg-gray-50')}
              `}
            >
              {editingId === todo._id ? (
                <div className="flex items-center space-x-2 w-full">
                  <input
                    type="text"
                    value={editText}
                    onChange={(e) => setEditText(e.target.value)}
                    className="flex-grow p-2 border rounded-lg"
                  />
                  <button
                    onClick={() => handleEditSubmit(todo)}
                    className="text-green-500 hover:bg-green-100 p-2 rounded-full"
                  >
                    <FaCheck />
                  </button>
                  <button
                    onClick={() => setEditingId(null)}
                    className="text-red-500 hover:bg-red-100 p-2 rounded-full"
                  >
                    <FaTimes />
                  </button>
                </div>
              ) : (
                <div className="flex-grow">
                  <div
                    onClick={() => onToggleComplete(todo._id)}
                    className={`cursor-pointer ${todo.completed ? 'line-through text-gray-500' : ''}`}
                  >
                    {todo.text}
                  </div>
                  {todo.description && (
                    <p className="text-sm text-gray-500 mt-1">{todo.description}</p>
                  )}
                </div>
              )}

              {editingId !== todo._id && (
                <div className="flex items-center space-x-2">
                  <button
                    onClick={() => handleEditStart(todo)}
                    className="text-blue-500 hover:bg-blue-100 p-2 rounded-full"
                  >
                    <FaEdit />
                  </button>
                  <button
                    onClick={() => onDelete(todo._id)}
                    className="text-red-500 hover:bg-red-100 p-2 rounded-full"
                  >
                    <FaTrash />
                  </button>
                </div>
              )}
            </motion.li>
          ))}
        </ul>
      </AnimatePresence>
    </div>
  );
}

export default TodoList;
